mapper.update:
update tb_sys_resources set status=-1
where id in

    #{id}

mapper.select:
SELECT r.id AS r_resourcesId, r.resources_name AS r_resourcesName, r.resources_url AS r_resourcesUrl, r.menu_id AS r_menuId, r.status as r_status, u.NAME AS r_creator, r.create_time AS r_createTime, m.id AS m_id, m.menu_url AS m_menuUrl, m.menu_name AS m_menuName FROM tb_sys_resources r LEFT JOIN ( SELECT * FROM tb_sys_menu WHERE STATUS !=- 1 ) m ON r.menu_id = m.id LEFT JOIN tb_sys_user u ON u.id = r.creator WHERE 1 = 1 AND r.STATUS != - 1
and r.resources_name like CONCAT('%', #{resourcesName,jdbcType=VARCHAR}, '%')
and r.id=#{id,jdbcType=VARCHAR}
and r.status=#{status,jdbcType=VARCHAR}
ORDER BY r.create_time DESC
