mapper.update:
update tb_sys_post set record_status = 0
where id in

    #{id}

mapper.select:
select id, post_name as postName, created_time as createdTime, record_status as recordStatus, post_type_id as postTypeId, (SELECT post_type_name from tb_sys_post_type where id = post_type_id and record_status = 1) as postTypeName from tb_sys_post where `record_status` != 0
and post_name like CONCAT('%', #{postName,jdbcType=VARCHAR}, '%')
and record_status = #{recordStatus,jdbcType=TINYINT}
and post_type_id = #{postTypeId,jdbcType=VARCHAR}
ORDER BY created_time desc,id desc
