mapper.update:
update tb_sys_post set status=-1
where id in

    #{id}

mapper.select:
select id, post_name as postName, create_time as createTime, `status`, (SELECT post_type_name from tb_sys_post_type where id = post_type_id and status = '1') as postTypeName from tb_sys_post where `status` != '-1'
and post_name like CONCAT('%', #{postName,jdbcType=VARCHAR}, '%')
and status = #{status,jdbcType=VARCHAR}
and post_type_id = #{postTypeId,jdbcType=VARCHAR}
ORDER BY create_time desc,id desc
