mapper.update:
update tb_sys_file set record_status = 0
where id in

    #{id}

mapper.select:
SELECT id as fileId, file_name as fileName, created_time as createdTime, creator_account as creatorAccount, file_url as fileUrl, record_status as recordStatus FROM `tb_sys_file` where `record_status` != 0
and file_name like CONCAT('%', #{fileName,jdbcType=VARCHAR}, '%')
and record_status=#{recordStatus,jdbcType=VARCHAR}
and file_url=#{fileUrl,jdbcType=VARCHAR}
ORDER BY created_time DESC , id DESC
