mapper.update:
update tb_sys_resources set status=-1
where id in

    #{id}

update tb_sys_resources set status=-1
where menu_id in

    #{id}

mapper.select:
SELECT DISTINCT r.id AS r_resourcesId, r.resources_name AS r_resourcesName, r.resources_url AS r_resourcesUrl, r.menu_id AS r_menuId, r.status as r_status, u.NAME AS r_creator, r.create_time AS r_createTime FROM tb_sys_resources r LEFT JOIN tb_sys_user u ON u.id = r.creator WHERE 1 = 1 AND r.STATUS != - 1
and r.resources_name like CONCAT('%', #{resourcesName,jdbcType=VARCHAR}, '%')
and r.id=#{id,jdbcType=VARCHAR}
and r.status=#{status,jdbcType=VARCHAR}
ORDER BY r.create_time DESC

select t.menu_name from tb_sys_menu t,tb_sys_resources tt
where t.id = tt.menu_id and tt.`status` != '-1'
and t.`status` = '1' and tt.id = #{id,jdbcType=VARCHAR}
