mapper.select:
SELECT tt.orgId ,tt.orgName ,tt.advisorAccount ,tt.advisorName ,tt.productId ,tt.productName ,tt.pictureUrl ,tt.evaluationAccount ,tt.evaluationTime ,tt.evaluationDesc ,tt.evaluationScore FROM ( SELECT sa.`org_id` AS "orgId" ,sa.`org_name` AS "orgName" ,sa.`advisor_account` AS "advisorAccount" ,sa.`advisor_name` AS "advisorName" ,sr.product_id as "productId" ,sr.`product_name` AS "productName" ,sp.`picture_url` AS "pictureUrl" ,concat_ws('****', substring(sr.`evaluator_account`, 1, 4), substring(sr.`evaluator_account`, 9, 3)) AS "evaluationAccount" ,date_format(sr.`created_time`,'%Y-%m-%d %H:%i:%s') AS "evaluationTime" ,sr.`evaluation_desc` AS "evaluationDesc" ,IFNULL(AVG(sr.`attitude_score`),0) AS "evaluationScore" FROM tb_service_advisor sa RIGHT JOIN tb_service_rating sr ON sa.`advisor_account` = sr.`advisor_account` and sa.approval_status='2' and sa.record_status='1' LEFT JOIN tb_service_product sp ON sr.`product_id`=sp.`product_id` GROUP BY sa.`org_id`,sa.`org_name`,sa.`advisor_account`,sa.`advisor_name`, sr.product_id,sr.`product_name`, sp.`picture_url`,sr.`evaluator_account`, sr.`created_time`,sr.`evaluation_desc` )tt WHERE 1=1
AND tt.orgId=#{orgId}
AND tt.productId=#{productId}
AND tt.advisorAccount=#{advisorAccount}
and tt.evaluationScore=5
and tt.evaluationScore>=3 and tt.evaluationScore<5
and tt.evaluationScore>=1 and tt.evaluationScore<3
ORDER BY tt.evaluationTime desc, tt.advisorAccount, tt.evaluationScore DESC

select count(1) as "evaluationTotal" ,count(case when tt.evaluationScore >4 then 1 end )as "praiseNum" ,count(case when tt.evaluationScore >=3 and tt.evaluationScore <=4 then 1 end ) as "averageNum" ,count(case when tt.evaluationScore >=0 and tt.evaluationScore <=2 then 1 end ) as "badReviewNum" FROM ( SELECT sa.`org_id` AS "orgId" ,sa.`org_name` AS "orgName" ,sa.`advisor_account` AS "advisorAccount" ,sa.`advisor_name` AS "advisorName" ,sr.product_id as "productId" ,sr.`product_name` AS "productName" ,sp.`picture_url` AS "pictureUrl" ,concat_ws('****', substring(sr.`evaluator_account`, 1, 4), substring(sr.`evaluator_account`, 9, 3)) AS "evaluationAccount" ,date_format(sr.`created_time`,'%Y-%m-%d %H:%i:%s') AS "evaluationTime" ,sr.`evaluation_desc` AS "evaluationDesc" ,IFNULL(AVG(sr.`attitude_score`),0) AS "evaluationScore" FROM tb_service_advisor sa RIGHT JOIN tb_service_rating sr ON sa.`advisor_account` = sr.`advisor_account` and sa.approval_status='2' and sa.record_status='1' LEFT JOIN tb_service_product sp ON sr.`product_id`=sp.`product_id` GROUP BY sa.`org_id`,sa.`org_name`,sa.`advisor_account`,sa.`advisor_name`, sr.product_id,sr.`product_name`, sp.`picture_url`,sr.`evaluator_account`, sr.`created_time`,sr.`evaluation_desc` )tt WHERE 1=1
AND tt.orgId=#{orgId}
AND tt.productId=#{productId}
AND tt.advisorAccount=#{advisorAccount}
and tt.evaluationScore=5
and tt.evaluationScore>=3 and tt.evaluationScore<5
and tt.evaluationScore>=1 and tt.evaluationScore<3
ORDER BY tt.evaluationTime desc, tt.advisorAccount, tt.evaluationScore DESC

