insert into tb_service_honor (id,advisor_account,certificate_name,certificate_type,issuing_agency ,get_time,certificate_photo,created_time,creator_account,modified_time,modifier_account,record_status) select A.* from(
( select #{item.id,jdbcType=VARCHAR} as 'id' ,#{item.advisorAccount,jdbcType=VARCHAR} as 'advisorAccount' ,#{item.certificateName,jdbcType=VARCHAR} as 'certificateName' ,#{item.certificateType,jdbcType=VARCHAR} as 'certificateType' ,#{item.issuingAgency,jdbcType=VARCHAR} as 'issuingAgency' ,#{item.getTime,jdbcType=VARCHAR} as 'getTime' ,#{item.certificatePhoto,jdbcType=VARCHAR} as 'certificatePhoto' ,#{item.createdTime,jdbcType=TIMESTAMP} as 'createdTime' ,#{item.creatorAccount,jdbcType=VARCHAR} as 'creatorAccount' ,#{item.modifiedTime,jdbcType=TIMESTAMP} as 'modifiedTime' ,#{item.modifierAccount,jdbcType=VARCHAR} as 'modifierAccount' ,1 as 'recordStatus' from dual )
)A

