mapper.select:
SELECT
FROM tb_service_recruit a
WHERE a.record_status = 1 AND a.id_ = #{id}

SELECT
FROM tb_service_recruit a
WHERE a.record_status = 1
AND a.com_id = #{comId}
AND a.status = #{status}
AND a.type = #{type}
AND ( a.post LIKE CONCAT('%', #{searchFiled}, '%') OR co.com_name LIKE CONCAT('%', #{searchFiled}, '%'))
AND DATE_SUB(DATE_FORMAT(CURDATE(),'%Y-%m-%d %H:%i:%S'), INTERVAL 6 DAY) <= DATE_FORMAT(a.created_time,'%Y-%m-%d %H:%i:%S')
AND DATE_SUB(DATE_FORMAT(CURDATE(),'%Y-%m-%d %H:%i:%S'), INTERVAL 1 MONTH) <= DATE_FORMAT(a.created_time,'%Y-%m-%d %H:%i:%S')
AND a.approval_status = #{approvalStatus}
AND DATE_FORMAT(#{beginDate},'%Y-%m-%d') <= DATE_FORMAT(a.created_time,'%Y-%m-%d') AND DATE_FORMAT(#{endDate},'%Y-%m-%d') >= DATE_FORMAT(a.created_time,'%Y-%m-%d')
ORDER BY a.view_count DESC, a.created_time DESC
ORDER BY a.created_time DESC

mapper.update:
UPDATE tb_service_recruit SET
    view_count = view_count + 1
WHERE id_ = #{id}

mapper.delete:
UPDATE tb_service_recruit SET
    record_status = 0,
    modifier_account = #{account},
    modified_time = #{modifiedTime}
WHERE id_ = #{id}
