mapper.select:
select tt.orgId ,tt.orgAccount ,tt.orgName ,tt.orgLogo ,tt.orgRegisterTime ,tt.isApprove ,tt.orgType ,orgPhone ,tt.orgSpeciality ,tt.orgAddress ,tt.pageviews ,tt.transactionNum ,tt.attitudeScore ,tt.evaluationNum ,tt.attitudeScore*#{praiseWeight}+tt.transactionNum*#{serviceWeight}+tt.pageviews*#{popularityWeight}+#{otherFactorScore} as "compreSort" from (select a.org_id_ as "orgId" ,a.org_account as "orgAccount" ,a.org_name as "orgName" ,a.org_speciality as "orgSpeciality" ,a.org_logo as "orgLogo" ,date_format(a.org_register_time,'%Y-%m-%d') as "orgRegisterTime" ,a.is_approve as "isApprove" ,a.org_type as "orgType" , i.org_phone as "orgPhone" ,CONCAT(i.org_province,i.org_city,i.org_area,i.org_address) as "orgAddress" ,ifnull(a.org_show,0) as "pageviews" ,(select count(1) from tb_service_require t where t.org_id_ = a.org_id_ and t.record_status = '1' and t.handle_result = '1' ) as "transactionNum" ,(SELECT IFNULL(AVG(t.`attitude_score`), 0) FROM tb_service_rating t WHERE t.org_id_ = a.org_id_) AS "attitudeScore" ,(SELECT COUNT(1) FROM tb_service_rating t WHERE t.org_id_ = a.org_id_) AS "evaluationNum" from tb_service_org a left join tb_service_org_info i on a.org_id_ = i.org_id_ LEFT JOIN tb_service_org_trait tr ON a.org_id_ = tr.org_id_ where 1=1 and a.record_status != '0' and a.org_status = '1'
AND a.business_type IN
#{item}
and a.business_type !=#{allowTechnology}
and a.business_s_type = #{businessSType}
and a.org_id_ in ( select distinct ssp.org_id_ from tb_service_product ssp where ssp.record_status='1' and ssp.template_id = #{templateId} )
and a.org_name like CONCAT('%', #{orgName}, '%')
and a.org_business = #{orgBusiness}
AND tr.trait_value IN
#{item}
GROUP BY a.org_id_ )tt
ORDER BY tt.pageviews DESC,tt.transactionNum DESC,tt.attitudeScore DESC
ORDER BY tt.attitudeScore*#{praiseWeight}+tt.transactionNum*#{serviceWeight}+tt.pageviews*#{popularityWeight}+#{otherFactorScore} DESC
ORDER BY tt.transactionNum DESC ,tt.pageviews DESC,tt.attitudeScore DESC
ORDER BY tt.attitudeScore DESC,tt.pageviews DESC,tt.transactionNum DESC

        SELECT COUNT(1) FROM tb_service_rating r WHERE r.product_id_ IN (
		    SELECT t.product_id_ FROM tb_service_product t WHERE  t.status = '1' AND t.record_status = '1'

                and t. template_id= #{productId}



                and t.signory_id = #{businessType}

		    GROUP BY t.product_id_
	    ) AND r.record_status = '1'
