mapper.select:
SELECT bill_id AS billId, ac_book_id AS acBookId, bill_name AS billName, bill_source AS billSource, bill_expense AS billExpense, reminder_number AS reminderNumber, CASE obj_type WHEN '1' THEN '企业' WHEN '2' THEN '个人' ELSE '其他' END objType, obj_id AS objId, obj_name AS objName, CASE payment_state WHEN '1' THEN '已支付' WHEN '2' THEN '待支付' ELSE '支付异常' END paymentState, CASE ac_book_type WHEN '1' THEN '电费' WHEN '2' THEN '物业费' WHEN '3' THEN '维修费' WHEN '4' THEN '房租费' WHEN '5' THEN '物品租赁费' WHEN '6' THEN '停车费' WHEN '7' THEN '车位费' WHEN '8' THEN '水费' WHEN '9' THEN '宣传费' WHEN '10' THEN '体检费' ELSE '其他' END acBookType, created_time AS createdTime, creator_account AS creatorAccount, late_payment AS latePayment, CASE payment_affirm WHEN '1' THEN '已确认' WHEN '2' THEN '未确认' ELSE '异常' END paymentAffirm, record_status AS recordStatus FROM tb_pay_bill WHERE 1=1 and record_status = 1 and bill_receiver = #{payBill.billReceiver,jdbcType=VARCHAR}
and bill_id = #{payBill.billId,jdbcType=VARCHAR}
and ac_book_type = #{payBill.acBookType,jdbcType=VARCHAR}
and payment_state = #{payBill.paymentState,jdbcType=VARCHAR}
order by payment_state desc
