mapper.select:
select t1.product_id as "productId", t1.product_name as "productName", t1.product_type as "productType", t1.signory_name as "signoryName", date_format(t1.release_time,"%Y-%M-%D %H:%i:%s") as "releaseTime", t1.status as "status", t2.org_name as "orgName", t3.con_phone as "conPhone", t3.con_name as "conName" from tb_service_product t1 left join tb_service_org t2 on t1.org_id = t2.ID left join tb_service_org_info t3 on t1.org_id = t3.org_id where t1.record_status = '1'
and t1.product_name like concat("%",#{productName},"%")
and t1.product_type = #{productType}
and t2.org_name like concat("%",#{orgName},"%")
and t1.signory_name like concat("%",#{signoryName},"%")
and t1.status = #{status}
and t1.release_time >= #{startTime}
and t1.release_time <= #{endTime}

select t1.product_id as "productId", t1.product_name as "productName", t1.signory_name as "signoryName", date_format(t1.release_time,"%Y-%M-%D %H:%i:%s") as "releaseTime", t1.status as "status", t2.org_name as "orgName", t3.con_phone as "conPhone", t3.con_name as "conName" from tb_service_product t1 left join tb_service_org t2 on t1.org_id = t2.ID left join tb_service_org_info t3 on t1.org_id = t3.org_id where t1.product_type = '1' and t1.record_status = '1'
and t1.product_name like concat("%",#{productName},"%")
and t1.product_type = #{productType}
and t2.org_name like concat("%",#{orgName},"%")
and t1.signory_name like concat("%",#{signoryName},"%")
and t1.status = #{status}
and t1.release_time >= #{startTime}
and t1.release_time <= #{endTime}
and t2.org_type= #{orgType}

mapper.update:
update tb_service_product t1 set t1.status = #{status},
t1.approval_time = now(),t1.approval_account = #{account}

,approval_comments = #{approvalComments}

 where t1.product_id = #{productId}
