mapper.select:
select sp.id_ as "id" ,sp.propaganda_type as "propagandaType" ,sp.propaganda_code as "propagandaCode" ,sp.propaganda_title as "propagandaTitle" ,sp.issue_platform as "issuePlatform" ,date_format(sp.effective_date,'%Y/%m/%d')as "effectiveDate" ,date_format(sp.invalid_date,'%Y/%m/%d')as "invalidDate" ,sp.poster_url as "posterUrl" ,sp.propaganda_fee as "propagandaFee" from tb_propaganda sp where 1=1
and sp.approval_status=#{bpp.approvalStatus}
and sp.propaganda_title like concat('%',#{bpp.searchContent},'%')
and sp.propaganda_type = #{bpp.propagandaType}
and sp.status=#{bpp.status}
and sp.creator_account=#{creator}
and sp.record_status=1 order by sp.sort,sp.created_time desc
