mapper.select:
select sp.id_ as "id" ,sp.propaganda_type as "propagandaType" ,sp.propaganda_code as "propagandaCode" ,sp.propaganda_title as "propagandaTitle" ,sp.propaganda_details as "propagandaDetails" ,sp.issue_platform as "issuePlatform" ,date_format(sp.effective_date,'%Y/%m/%d')as "effectiveDate" ,date_format(sp.invalid_date,'%Y/%m/%d')as "invalidDate"
,#{bpp.approvalStatus} as "approvalStatus"
,case when sp.approval_status=0 and sp.is_pay=0 and sp.status=0 then "0" when sp.approval_status=1 and sp.is_pay=0 and sp.status=0 then "1" when sp.approval_status=3 then "3" when sp.approval_status=2 and sp.is_pay=0 and sp.status=0 then "4" when sp.approval_status=2 and sp.is_pay=1 and sp.status=0 then "5" when sp.approval_status=2 and sp.is_pay=1 and sp.status=1 then "6" when sp.approval_status=2 then "2" when sp.record_status=0 then "7" end as "approvalStatus"
,sp.propaganda_area as "propagandaArea" ,sp.poster_url as "posterUrl" ,sp.propaganda_fee as "propagandaFee" ,sp.status as "status" ,sp.view_count as "viewCount" ,date_format(sp.created_time,'%Y-%m-%d %H:%i:%s') as "createdTime" ,sc.code_name as "propagandaTypeName" from tb_propaganda sp left join tb_service_code sc on sp.propaganda_type=sc.code_value where 1=1
and sp.approval_status=#{bpp.approvalStatus}
and sp.approval_status='2' and sp.is_pay='0' and sp.status='0'
and sp.approval_status='2' and sp.is_pay='1' and sp.status='0'
and sp.approval_status='2' and sp.is_pay='1' and sp.status='1'
and sp.record_status=0
and sp.propaganda_title like concat('%',#{bpp.searchContent},'%')
and sp.propaganda_type = #{bpp.propagandaType}
and sp.status=#{bpp.status}
and sp.issue_platform=#{bpp.issuePlatform}
and sp.propaganda_area=#{bpp.propagandaArea}
and sp.creator_account=#{creator}
and sp.record_status=1
order by sp.created_time desc,sp.sort

mapper.update:
UPDATE tb_propaganda SET
  view_count = view_count + 1
WHERE id_ = #{id}
