mapper.select:
SELECT sr.`req_num` AS "reqNum" ,sr.`org_id` AS "orgId" ,sr.`org_name`AS "orgName" ,sr.`business_id` AS "businessId" ,sr.`business_area` AS "businessArea" ,sr.`product_id` AS "productId" ,sr.`product_name` AS "productName" ,sr.`advisor_account` AS "advisorAccount" ,sr.`advisor_name` AS "advisorName" ,DATE_FORMAT(sr.`created_time`,"%Y/%m/%d")AS requireTime ,CASE sr.`status` WHEN '1' THEN "待处理" WHEN '2' THEN "已处理" ELSE '--' END AS "status" ,CASE sr.handle_result WHEN '1' THEN "对接成功" WHEN '2' THEN "对接失败" WHEN '3' THEN "企业需求撤销" WHEN '4' THEN "未对接" ELSE '--' END AS "handleResult" FROM tb_service_require sr WHERE 1=1 AND sr.issue_account=#{account}
and (sr.product_id like CONCAT('%', #{intentProduct}, '%') or sr.product_name like CONCAT('%', #{intentProduct}, '%'))
and (sr.org_id like CONCAT('%', #{intentOrg}, '%') or sr.product_name like CONCAT('%', #{intentOrg}, '%'))
and sr.handle_result=#{handleResult}
