mapper.select:
SELECT sr.req_num AS "reqNum" ,sr.`business_id` AS "businessId" ,sr.`business_area` AS "businessArea" ,sr.`org_id_` AS "orgId" ,sr.`org_name` AS "orgName" ,IFNULL(sr.`advisor_account`,"未指定") AS"advisorAccount" ,IFNULL(sr.`advisor_name`,"未指定") AS "advisorName" ,sr.issue_account AS "issueAccount" ,sr.`product_id_` AS "productId" ,sr.`product_name` AS "productName" ,CASE sp.product_type WHEN '0' THEN "常规服务产品" WHEN '1' THEN "特色服务产品" ELSE '' END AS "productType" ,DATE_FORMAT(sr.`issue_time`,"%Y/%m/%d")AS requireTime ,CASE sr.handle_result WHEN '1' THEN "对接成功" WHEN '2' THEN "对接失败" WHEN '3' THEN "企业需求撤销" WHEN '4' THEN "未对接" ELSE '--' END AS "handleResult" FROM tb_service_require sr LEFT JOIN tb_service_product sp ON sr.product_id_=sp.product_id_ where 1=1
and sp.product_type=#{productType}
and sr.handle_result=#{handleResult}
and sr.business_id=#{businessId}
and (sr.org_id_ like CONCAT('%', #{serviceOrg}, '%') or sr.org_name like CONCAT('%', #{serviceOrg}, '%'))
and date_format(sr.`issue_time`,'%Y%m')>=#{requireStartTime}
and date_format(sr.`issue_time`,'%Y%m')<=#{requireEndTime}
and (sr.product_id_ like CONCAT('%', #{serviceProduct}, '%') or sr.product_name like CONCAT('%', #{serviceProduct}, '%'))
ORDER BY sr.`issue_time` DESC
