mapper.select:
		SELECT job_number, department_id, department,name, user_id, sum(attendance) attendance, 
      sum(late),sum(leave_early) leave_early,sum(start_miss_card) start_miss_card, 
     sum(end_miss_card)  end_miss_card,sum(absenteeism) absenteeism,sum(compassionate_leave) compassionate_leave, 
      sum(sick_leave) sick_leave, sum(marriage_holiday) marriage_holiday,sum(funeral_leave) funeral_leave, 
     sum(maternity_leave) maternity_leave,sum(annual_leave) annual_leave FROM tb_manpower_attendance_management t1
		where 1=1

			and t1.department = #{department,jdbcType=VARCHAR}


			and t1.department_id = #{departmentId,jdbcType=VARCHAR}


			and t1.name like concat('%',#{name},'%')


			and t1.attendance_date >= #{startTime,jdbcType=VARCHAR}


			and t1.attendance_date <= #{endTime,jdbcType=VARCHAR}

		group by job_number

SELECT job_number, department_id, department,name, user_id, sum(attendance) attendance, sum(late),sum(leave_early) leave_early,sum(start_miss_card) start_miss_card, sum(end_miss_card) end_miss_card,sum(absenteeism) absenteeism,sum(compassionate_leave) compassionate_leave, sum(sick_leave) sick_leave, sum(marriage_holiday) marriage_holiday,sum(funeral_leave) funeral_leave, sum(maternity_leave) maternity_leave,sum(annual_leave) annual_leave FROM tb_manpower_attendance_management t1 where 1=1
and t1.department = #{department,jdbcType=VARCHAR}
and t1.name like concat('%',#{name},'%')
and t1.attendance_date >= #{startTime,jdbcType=VARCHAR}
and t1.attendance_date <= #{endTime,jdbcType=VARCHAR}
group by attendance_date
