mapper.select:
SELECT * FROM tb_manpower_examina_result_info
WHERE
examina_id = #{examinaId,jdbcType=VARCHAR}
AND job_number =
#{jobNumber,jdbcType=VARCHAR}

SELECT t1.id resultId,t2.id,t2.examina_name,t1.job_number,t1.`name`, t1.use_time,t1.achievement,t1.is_adopt, CASE t1.is_adopt WHEN 1 THEN '是' ELSE '否' END isAdoptStr, t1.examina_method, CASE t1.examina_method WHEN 1 THEN '电脑' ELSE '手机' END examinaMethodStr, t1.examina_start_time,t1.examina_end_time, t1.rank,t2.total_score,t2.pass_score FROM tb_manpower_examina_result_info t1,tb_manpower_examina_management t2 WHERE t1.examina_id=t2.id and t1.record_status != 0
and t1.examina_id = #{examinaId,jdbcType=VARCHAR}
and t1.name like CONCAT('%',#{name,jdbcType=VARCHAR},'%')
