mapper.select:
SELECT t1.project_id,t1.research_project, t1.effective_time_start,t1.effective_time_end, t1.creator_account,t1.`status`, CASE WHEN t1.`status`=0 THEN '草稿' WHEN t1.`status`=1 THEN '进行中' ELSE '已结束' END statusStr FROM tb_manpower_train_investiga t1 WHERE t1.record_status != 0
and t1.research_project like CONCAT('%',#{researchProject,jdbcType=VARCHAR},'%')
and t1. effective_time_start >= #{effectiveTimeStart,jdbcType=TIMESTAMP}
and t1.effective_time_end <= #{effectiveTimeEnd,jdbcType=TIMESTAMP}
ORDER BY t1.effective_time_start,t1.effective_time_end DESC

SELECT t1.project_id,t1.research_project, t1.effective_time_start,t1.effective_time_end, t1.creator_account,t1.`status`, CASE WHEN t1.`status`=0 THEN '草稿' WHEN t1.`status`=1 THEN '进行中' ELSE '已结束' END statusStr FROM tb_manpower_train_quest_investiga t1 WHERE t1.record_status != 0
and t1.research_project like CONCAT('%',#{researchProject,jdbcType=VARCHAR},'%')
and t1. effective_time_start >= #{effectiveTimeStart,jdbcType=TIMESTAMP}
and t1.effective_time_end <= #{effectiveTimeEnd,jdbcType=TIMESTAMP}
ORDER BY t1.effective_time_start,t1.effective_time_end DESC
