mapper.update:
update tb_oa_meeting set meeting_status = 1, modified_time = now() where id in (
select t.id from ( SELECT round( ( UNIX_TIMESTAMP( meeting.start_time ) - UNIX_TIMESTAMP( NOW( ) ) ) / 60 ) as sub_time, meeting.id FROM tb_oa_meeting meeting WHERE meeting.meeting_status =0
)t where t.sub_time<=1 )

update tb_oa_meeting set meeting_status =2, modified_time = now() where id in (
select t.id from ( SELECT round( ( UNIX_TIMESTAMP( meeting.start_time ) - UNIX_TIMESTAMP( NOW( ) ) ) / 60 ) as sub_time, meeting.id FROM tb_oa_meeting meeting WHERE meeting.meeting_status =1
)t where t.sub_time<=-10 )

mapper.select:
SELECT
FROM tb_oa_meeting meeting LEFT JOIN ( select c.meeting_id,c.content from tb_oa_meeting_content c where c.record_status!=0 )tc on tc.meeting_id=meeting.id LEFT JOIN (
SELECT m.id AS meetingroom_id, m.NAME , m.building , m.floor , m.room_number , m.position FROM tb_oa_meeting_room m WHERE m.record_status !=0
)meetingroom on meetingroom.meetingroom_id=meeting.meeting_room_id LEFT JOIN ( SELECT t.id, t.NAME, t.account, tt.department_name FROM tb_sys_user t LEFT JOIN ( SELECT t1.user_id, t2.department_name FROM tb_sys_user_department_post t1 INNER JOIN tb_sys_department t2 ON t1.department_id = t2.id WHERE t1.is_default = 1 AND t1.record_status = 1 AND t2.record_status = 1 ) tt ON t.id = tt.user_id WHERE t.record_status = 1 ) u ON meeting.applicant = u.id WHERE 1=1
and meeting.id =#{id,jdbcType=VARCHAR}
and meeting.title like CONCAT('%', #{title,jdbcType=VARCHAR}, '%')
and meeting.approval_status = #{approvalStatus,jdbcType=VARCHAR}
and meeting.meeting_status = #{meetingStatus,jdbcType=VARCHAR}
and meeting.meeting_room_id =#{meetingRoomId,jdbcType=VARCHAR}
and meetingroom.name like CONCAT('%', #{meetingRoomName,jdbcType=VARCHAR}, '%')
and u.department_name like CONCAT('%', #{departmentName,jdbcType=VARCHAR}, '%')
AND Date(meeting.start_time) >= #{startTime} AND Date(meeting.end_time) <= #{endTime}
and (meeting.meeting_status=0 or meeting.meeting_status=1 ) AND (meeting.start_time >= #{currentTime} or (meeting.end_time >= #{currentTime} and meeting.start_time <= #{currentTime}) ) AND Date(meeting.start_time) = DATE_FORMAT(#{currentTime},'%Y-%m-%d')
ORDER BY
meeting.start_time asc ,
meeting.created_time DESC, meeting.id DESC

select
meeting.id AS meeting_id, meeting.work_order_num AS meeting_work_order_num, meeting.start_time AS meeting_start_date, meeting.start_time AS meeting_start_time, meeting.end_time AS meeting_end_time, meeting.meeting_room_id AS meeting_meeting_room_id, meeting.approval_status AS meeting_approval_status, meeting.record_status AS meeting_record_status, meeting.creator_account AS meeting_creator_account, meeting.created_time AS meeting_created_time, meeting.modifier_account AS meeting_modifier_account, meeting.modified_time AS meeting_modified_time, meeting.user_id_str AS meeting_user_id_str, meeting.applicant AS meeting_applicant, meeting.application_time AS meeting_application_time, meeting.sign_in_QR AS meeting_sign_in_QR, meeting.wechat_remind AS meeting_wechat_remind, meeting.message_remind AS meeting_message_remind, meeting.pc_remind AS meeting_pc_remind, meeting.app_remind AS meeting_app_remind, meeting.is_remind AS meeting_is_remind, meeting.title AS meeting_title, meeting.approval_role AS meeting_approval_role, meeting.approval_user AS meeting_approval_user, meeting.approval_opinion AS meeting_approval_opinion, meeting.meeting_status AS meeting_meeting_status, u.NAME AS userName, u.department_name AS departmentName, p. meetingparticipants_id, p.meetingparticipants_meeting_id, p.meetingparticipants_meeting_user_id, p. meetingparticipants_attendance_status, p.name as meetingparticipants_userName, p.account as meetingparticipants_account,
meetingroom.meetingroom_id, meetingroom.name as meetingroom_name, meetingroom.building as meetingroom_building, meetingroom.floor as meetingroom_floor, meetingroom.room_number as meetingroom_room_number, meetingroom.position as meetingroom_position, tc.content as content FROM tb_oa_meeting meeting LEFT JOIN ( select c.meeting_id,c.content from tb_oa_meeting_content c where c.record_status!=0 )tc on tc.meeting_id=meeting.id LEFT JOIN ( SELECT meetingparticipants.id AS meetingparticipants_id, meetingparticipants.meeting_id AS meetingparticipants_meeting_id, meetingparticipants.meeting_user_id AS meetingparticipants_meeting_user_id, meetingparticipants.attendance_status AS meetingparticipants_attendance_status, tu.name, tu.account FROM tb_oa_meeting_participants meetingparticipants LEFT JOIN ( SELECT id, NAME, account FROM tb_sys_user where record_status =1) tu on tu.id=meetingparticipants.meeting_user_id WHERE meetingparticipants.record_status != 0 )p on p.meetingparticipants_meeting_id=meeting.id LEFT JOIN (
SELECT m.id AS meetingroom_id, m.NAME , m.building , m.floor , m.room_number , m.position FROM tb_oa_meeting_room m WHERE m.record_status !=0
)meetingroom on meetingroom.meetingroom_id=meeting.meeting_room_id LEFT JOIN ( SELECT t.id, t.NAME, t.account, tt.department_name FROM tb_sys_user t LEFT JOIN ( SELECT t1.user_id, t2.department_name FROM tb_sys_user_department_post t1 INNER JOIN tb_sys_department t2 ON t1.department_id = t2.id WHERE t1.is_default = 1 AND t1.record_status = 1 AND t2.record_status = 1 ) tt ON t.id = tt.user_id WHERE t.record_status = 1 ) u ON meeting.applicant = u.id WHERE 1=1 and meeting.id=#{id,jdbcType=VARCHAR}
