mapper.select:
select t.type_id as typeId ,t.type_name as typeName ,t.state as state ,t.create_account as createUserId ,t.create_time as createTime ,t.update_account as updateUserId ,t.update_time as updateTime ,count(f.type_id) as fileTotal from tb_activity_type t left join tb_activity_file f on t.type_id = f.type_id where 1=1 and t.state != '-1'
and t.state = #{state,jdbcType=VARCHAR}
group by t.type_id
