mapper.select:
SELECT tb.cost_type_id as costTypeId, max(tb.cost_type_name) as costTypeName, tb.department_id as departmentId, max(tb.department_name) as departmentName, #{startMonth,jdbcType=VARCHAR} as startMonth, #{endMonth,jdbcType=VARCHAR} as endMonth FROM `tb_finance_total_budget` tb LEFT JOIN tb_finance_type t ON t.id = tb.cost_type_id WHERE tb.record_status = 1 AND t.record_status = 1 AND tb.budget_month BETWEEN #{startMonth,jdbcType=VARCHAR} AND #{endMonth,jdbcType=VARCHAR}
and t.id=#{costTypeId,jdbcType=INTEGER}
and tb.department_id in
#{item,jdbcType=VARCHAR}
GROUP BY tb.cost_type_id, tb.department_id ORDER BY t.show_order

SELECT th.cost_type_id as costTypeId, max(th.cost_type_name) as costTypeName, th.department_id as departmentId, max(th.department_name) as departmentName, th.created_time, th.budget_Type, case th.budget_Type when 0 then '年初预算' else '临时预算'end as budget_Type_name, #{startMonth,jdbcType=VARCHAR} as startMonth, #{endMonth,jdbcType=VARCHAR} as endMonth FROM `tb_finance_budget_history` th LEFT JOIN tb_finance_type t ON t.id = th.cost_type_id WHERE th.record_status = 1 AND t.record_status = 1 AND th.budget_month BETWEEN #{startMonth,jdbcType=VARCHAR} AND #{endMonth,jdbcType=VARCHAR}
and t.id=#{costTypeId,jdbcType=INTEGER}
and th.department_id in
#{item,jdbcType=VARCHAR}
GROUP BY th.cost_type_id, th.department_id ORDER BY t.show_order

select department_id departmentId,cost_type_id costTypeId,budget_month budgetMonth,sum(budget_money) budgetMoney from tb_finance_budget_history bh

  record_status=1 and cost_type_id=#{costTypeId,jdbcType=INTEGER} and department_id=#{departmentId,jdbcType=VARCHAR} and
    budget_month =#{budgetMonth,jdbcType=VARCHAR}

group by department_id,cost_type_id,budget_month ;
