insert into tb_activity_file (id,type_id,file_src,create_account,create_time,status)
select A.* from(

    ( select #{item.id,jdbcType=VARCHAR}
    ,#{item.typeId,jdbcType=VARCHAR}
    ,#{item.fileSrc,jdbcType=VARCHAR}
    ,#{item.createAccount,jdbcType=VARCHAR}
    ,#{item.createTime,jdbcType=TIMESTAMP}
    ,#{item.status,jdbcType=VARCHAR}
    from dual)

)A
