mapper.select:
select a.id as "id", a.acti_type as "actiType", a.acti_name as "actiName", date_format(a.acti_start_time,'%Y-%m-%d %H:%i:%S') as "actiStartTime", date_format(a.acti_end_time,'%Y-%m-%d %H:%i:%S') as "actiEndTime", date_format(a.apply_start_time,'%Y-%m-%d %H:%i:%S') as "applyStartTime", date_format(a.apply_end_time,'%Y-%m-%d %H:%i:%S') as "applyEndTime", date_format(a.mes_send_time,'%Y-%m-%d %H:%i:%S') as "mesSendTime", a.acti_address as "actiAddress",a.status as "status",a.is_index as "isIndex", a.acti_cost as "actiCost", a.acti_organizer as "actiOrganizer", a.acti_number as "actiNumber", a.acti_poster_url as "actiPosterUrl", date_format(a.create_time,'%Y-%m-%d %H:%i:%S') as "createTime", a.create_account as "createAccount", date_format(a.update_time,'%Y-%m-%d %H:%i:%S') as "updateTime", a.update_account as "updateAccount", a.acti_views as "actiViews", a.acti_like as "actiLike", a.apply_num as "applyNum", a.partic_num as "particNum",a.is_apply as "isApply",a.park_id as "parkId",co.code_name as "parkName", a.acti_order as "actiOrder",t.type_name as "typeName",a.show_apply_num as "showApplyNum",a.apply_check as "applyCheck" from tb_activity a left join tb_activity_type t on a.acti_type = t.type_id left join tb_park_code co on co.id = a.park_id where 1=1 and a.status != '5'
and a.acti_type = #{actiType}
and a.status = #{status}
and a.acti_name LIKE concat(concat('%',#{actiName}),'%')
and a.is_index = #{isIndex}
and a.acti_address = #{actiAddress}
and a.acti_cost = #{actiCost}
and a.acti_organizer = #{actiOrganizer}
and a.show_apply_num = #{showApplyNum}
order by a.create_time desc

select ta.id as "id" ,ta.acti_name as "actiName" ,ta.acti_address as "actiAddress" ,date_format(ta.acti_start_time,'%Y-%m-%d %H:%i:%s') as "actiStartTime" ,date_format(ta.acti_end_time,'%Y-%m-%d %H:%i:%s') as "actiEndTime" ,ta.apply_num as "applyNum" ,ta.acti_like as "actiLike" ,ta.acti_number as "actiNumber" ,ta.acti_poster_url as "actiPosterUrl" ,ta.show_apply_num as "showApplyNum" from tb_activity ta left join tb_activity_detail td on td.activity_id = ta.id where 1=1 and ta.status not in ('1','5')
and ta.acti_type = #{typeId,jdbcType=VARCHAR}
and ta.acti_start_time >= #{startTime,jdbcType=VARCHAR}
and ta.acti_start_time <= #{endTime,jdbcType=VARCHAR}
and ta.acti_name LIKE concat('%',#{keyWord},'%') or ta.acti_address LIKE concat('%',#{keyWord},'%') or td.acti_detail LIKE concat('%',#{keyWord},'%')
order by
${orderBy} desc,
ta.acti_order desc
