mapper.select:
select t1.id as "id" ,t1.root_id as "rootId" ,t1.p_id as "pId" ,t1.creator_account as "creatorAccount" ,t1.parent_account as "parentAccount" ,date_format(t1.created_time, '%Y-%m-%d %H:%i:%s') as "createdTime" ,t1.like_num as "likeNum" ,t1.com_status as "comStatus" ,t1.com_type as "comType" ,t1.com_content as "comContent" ,case when t5.creator_account = #{loginAccount} and t5.like_status='1' then 'true' else 'false' end as 'isCommentLike' from tb_comment t1 left join tb_comment t4 on t1.id=t4.p_id left join tb_park_like t5 on t1.id=t5.like_parent_id where 1=1 and t1.root_id=#{activityId} and t1.p_id in
#{id}
and t1.com_status='1' order by t1.created_time
