mapper.select:
select a.id as "id", a.acti_type as "actiType", a.acti_name as "actiName", date_format(a.acti_start_time,'%Y-%m-%d %H:%i:%S') as "actiStartTime", date_format(a.acti_end_time,'%Y-%m-%d %H:%i:%S') as "actiEndTime", date_format(a.apply_start_time,'%Y-%m-%d %H:%i:%S') as "applyStartTime", date_format(a.apply_end_time,'%Y-%m-%d %H:%i:%S') as "applyEndTime", date_format(a.mes_send_time,'%Y-%m-%d %H:%i:%S') as "mesSendTime", a.acti_address as "actiAddress",a.acti_status as "actiStatus",a.is_index as "isIndex", a.acti_cost as "actiCost", a.acti_organizer as "actiOrganizer", a.acti_number as "actiNumber", a.acti_poster_url as "actiPosterUrl", date_format(a.created_time,'%Y-%m-%d %H:%i:%S') as "createdTime", a.creator_account as "creatorAccount", date_format(a.modified_time,'%Y-%m-%d %H:%i:%S') as "modifiedTime", a.modifier_account as "modifierAccount", a.acti_views as "actiViews", a.acti_like as "actiLike", a.apply_num as "applyNum", a.partic_num as "particNum",a.is_apply as "isApply",a.park_id as "parkId",co.code_name as "parkName", a.acti_order as "actiOrder",t.type_name as "typeName",a.show_apply_num as "showApplyNum",a.apply_check as "applyCheck" from tb_activity a left join tb_activity_type t on a.acti_type = t.type_id left join tb_park_code co on co.id = a.park_id where 1=1 and a.record_status != '0'
and a.acti_type = #{actiType}
and a.acti_status = #{actiStatus}
and a.acti_name LIKE concat(concat('%',#{actiName}),'%')
and a.is_index = #{isIndex}
and a.acti_address = #{actiAddress}
and a.acti_cost = #{actiCost}
and a.acti_organizer = #{actiOrganizer}
and a.show_apply_num = #{showApplyNum}
order by a.created_time desc

select t1.id as "id" ,t1.acti_name as "actiName" ,t3.type_name as "typeName" ,t1.acti_cost as "actiCost" ,t1.acti_organizer as "actiOrganizer" ,t2.code_name as "parkName" ,t1.acti_address as "actiAddress" , date_format(t1.acti_start_time,'%Y-%m-%d %H:%i:%s') as "actiStartTime" ,t1.acti_status as "actiStatus"
from tb_activity t1 left join tb_park_code t2 on t2.id =t1.park_id left join tb_activity_type t3 on t3.type_id = t1.acti_type where t1.record_status ='1' and t1.acti_status != '1' and t1.id in ( select activity_id from tb_activity_apply where creator_account = #{account} and apply_status=#{applyStatus} )
and t1.acti_name like concat('%', #{actiName},'%')
