select t1.topic_id as "topicId" ,DATE_FORMAT(t1.created_time,'%Y-%m-%d %H:%i:%s') as "createdTime" ,t1.topic_content as "topicContent" ,t1.creator_account as "creatorAccount" ,t1.identity as "identity" ,IFNULL((SELECT SUM(1) FROM tb_comment com WHERE com.root_id = t1.topic_id ),0) AS "commentsNum" ,IFNULL((SELECT SUM(1) FROM tb_park_like li WHERE li.like_parent_id = t1.topic_id and li.like_status = "1" and li.record_status = 1),0) AS "likeNum" ,ifnull((SELECT SUM(1) FROM tb_person_care tc WHERE tc.care_account= #{currentAccount} and tc.receive_account = t1.creator_account ),0) AS "careStatus" ,group_concat(t2.img_url) as "imgUrl" from tb_gam_topic t1 left join tb_file_img t2 on t1.topic_id = t2.topic_id where t1.topic_status = "1" and t1.record_status = 1 and (t1.topic_type = "1"
or (t1.topic_type = "2" and t1.creator_account in
#{item}
)
) group by t1.topic_id order by t1.created_time desc

select t1.topic_id as "topicId" ,DATE_FORMAT(t1.created_time,'%Y-%m-%d %H:%i:%s') as "createdTime" ,t1.topic_content as "topicContent" ,t1.creator_account as "creatorAccount" ,t1.identity as "identity" ,IFNULL((SELECT SUM(1) FROM tb_comment com WHERE com.root_id = t1.topic_id ),0) AS "commentsNum" ,IFNULL((SELECT SUM(1) FROM tb_park_like li WHERE li.like_parent_id = t1.topic_id and li.like_status = "1" and li.record_status = 1) ),0) AS "likeNum" ,group_concat(t2.img_url) as "imgUrl" from tb_gam_topic t1 left join tb_file_img t2 on t1.topic_id = t2.topic_id where t1.topic_status = "1" and t1.record_status = 1 and t1.creator_account = #{account}
and t1.topic_type = "1" or t1.topic_type = "2"
group by t1.topic_id order by t1.created_time desc

select t1.creator_account as "creatorAccount" ,t1.id as "commentId" ,t1.com_content as "comContent" ,t1.p_id as "parentId" ,date_format(t1.created_time,'%Y-%m-%d %H:%i:%s') as "createdTime" ,IFNULL((SELECT SUM(1) FROM tb_park_like li WHERE li.like_parent_id = t1.id and li.like_status = "1" and li.record_status = 1)),0) AS "likeNum" from tb_comment t1 where 1=1
