mapper.select:
select t1.notice_id as "noticeId" ,t1.notice_title as "noticeTitle" ,t1.notice_serial as "noticeSerial" ,DATE_FORMAT(t1.start_time,"%Y-%m-%d") as "startTime" ,DATE_FORMAT(t1.end_time,"%Y-%m-%d") as "endTime" ,t1.home_popup as "homePopup" ,t1.home_show as "homeShow" ,t1.platform_type as "platformType" ,t1.platform_name as "platformName" ,t1.publish_name as "pulishName" ,DATE_FORMAT(t1.created_time,"%Y-%m-%d %H:%i:%s") as "createdTime" ,t1.notice_status as "noticeStatus" ,t1.sort_field as "sortField" ,t2.notice_details as "noticeDetails" from tb_notice_info t1 left join tb_notice_details t2 on t1.notice_id=t2.notice_id where 1=1 and t1.record_status = 1
and t1.notice_status = #{noticeStatus}
and t1.home_popup = #{homePopup}
and t1.home_show = #{homeShow}
and t1.notice_title like CONCAT("%",#{keyWord},"%") or t1.platform_name like CONCAT("%",#{keyWord},"%")
order by t1.sort_field,t1.created_time desc
