mapper.select:
select t.* from ( select a.area_id_ as "areaId" ,a.area_name as "areaName" ,a.area_address as "areaAddress" ,a.longitude as "longitude" ,a.latitude as "latitude" ,a.leisure_number as "leisureNumber" ,a.rent_number as "rentNumber" ,a.temp_number as "tempNumber"
,ROUND( 6378.138 * 2 * ASIN( SQRT( POW( SIN( ( CONVERT(#{latitude,jdbcType=VARCHAR},DECIMAL) * PI() / 180 - a.latitude * PI() / 180 ) / 2 ), 2 ) + COS(CONVERT(#{latitude,jdbcType=VARCHAR},DECIMAL) * PI() / 180) * COS(a.latitude * PI() / 180) * POW( SIN( ( CONVERT(#{longitude,jdbcType=VARCHAR},DECIMAL) * PI() / 180 - a.longitude * PI() / 180 ) / 2 ), 2 ) ) ) * 1000 ) AS "distance" ,a.parking_total as "parkingTotal" ,a.temp_price_depict as "tempPriceDepict" ,a.temp_price as "tempPrice" ,a.temp_price_unit as "tempPriceUnit" ,a.temp_price_total as "tempPriceTotal" ,a.temp_free_time as "tempFreeTime" ,a.rent_price as "rentPrice" ,a.area_picture as "areaPicture" ,a.space_picture as "spacePicture" ,a.gate_factory as "gateFactory" ,a.gate_id as "gateId" from tb_parking_area a where a.record_status = '1' and area_status = '1'
and a.area_name = #{areaName,jdbcType=VARCHAR}
and a.area_address = #{areaAddress,jdbcType=VARCHAR}
)t order by t.distance
