mapper.select:
select sp.policy_id as "policyId" ,sp.policy_code as "policyCode" ,sp.policy_level_code as "policyLevelCode" ,sp.policy_level_name as "policyLevelName" ,sp.policy_class_code as "policyClassCode" ,sp.policy_class_name as "policyClassName" ,sp.policy_type as "policyType" ,sp.policy_diagram_url as "policyDiagramUrl" ,sp.thematic_type as "thematicType" ,sp.policy_title as "policyTitle" ,sp.policy_issu_num as "policyIssuNum" ,sp.read_num as "readNum" ,date_format(sp.release_date,'%Y-%m-%d') as "releaseDate" ,sp.status as "status" ,sp.support_method as "supportMethod" ,sp.support_industry as "supportIndustry" ,sp.issue_unit as "issueUnit" ,case when spd.policy_content!=null and spd.policy_content!='' then concat(substring(spd.policy_content,100),'...') else null end as "briefContent" ,sp.is_policy_diagram as "isPolicyDiagram" ,sp.relation_policy_diagram_id as "relationPolicyDiagramId" ,sp.is_policy_original as "isPolicyOriginal" ,sp.relation_policy_original_id as "relationPolicyOriginalId" from tb_policy sp left join tb_policy_details spd on sp.policy_id=spd.policy_id where 1=1 and sp.record_status='1' and sp.status='1'
and sp.policy_level_code=#{policyCenterHomeParam.policyLevelCode}
and sp.policy_class_code=#{policyCenterHomeParam.policyClassCode}
and sp.policy_type=#{policyCenterHomeParam.policyType}
and date_format(sp.release_date,'%Y')=#{policyCenterHomeParam.releaseDate}
and date_format(sp.release_date,'%Y')<=#{policyCenterHomeParam.releaseDate}
and sp.policy_title like concat('%',#{policyCenterHomeParam.policySearch},'%')
and sp.thematic_type =#{thematicType}
order by sp.release_date desc
