mapper.select:
select sp.policy_title as "policyTitle" ,sp.read_num as "readNum" ,sp.policy_level_name as "policyLevelName" ,sp.policy_class_name as "policyClassName" ,date_format(sp.release_date,'%Y/%m/%d %H:%i:%s') as "releaseDate" ,sp.support_method as "supportMethod" ,sp.support_industry as "supportIndustry" ,sp.issue_unit as "issueUnit" from tb_policy sp where 1=1 and sp.record_status='1' and sp.policy_type='0'
and sp.status=#{status}
and sp.policy_title like concat('%',#{policyTitle},'%')

select sp.policy_title as "policyTitle" ,sp.policy_diagram_url as "policyDiagramUrl" ,sp.read_num as "readNum" ,date_format(sp.release_date,'%Y/%m/%d %H:%i:%s') as "releaseDate" ,sp.relation_policy_original_id as "relationPolicyOriginalId" ,IFNULL(spp.policy_title,"无") as "relationPolicyOriginalName" from tb_policy sp left join tb_policy spp on sp.policy_id=spp.relation_policy_original_id where 1=1 and sp.policy_type="1"
and sp.status=#{status}
and sp.policy_title like concat('%',#{policyTitle},'%')
