mapper.select:
select b.bill_id as "billId", b.bill_num as "billNum", b.bill_name as "billName", b.bill_type as "billType", c.code_value as "billTypeName", b.bill_obj_id as "billObjId", b.bill_obj_name as "billObjName", b.bill_obj_type as "billObjType", b.bill_amount as "billAmount", b.remind_id as "remindId", date_format(b.bill_create_time,'%Y-%m-%d %H:%i:%s') as "billCreateTime", date_format(b.pay_end_time,'%Y-%m-%d %H:%i:%s') as "payEndTime", date_format(b.urge_time,'%Y-%m-%d %H:%i:%s') as "urgeTime", b.bill_create_account as "billCreateAccount", b.bill_remark as "billRemark", CASE b.bill_status WHEN '-1' THEN "账单审核不通过" WHEN '0' THEN "待审核" WHEN '1' THEN "待支付" WHEN '2' THEN "已支付" WHEN '3' THEN "支付失败" WHEN '4' THEN "已确认支付" WHEN '5' THEN "支付审核失败" ELSE '-' END AS "billStatus", CASE b.bill_pay_type WHEN '10' THEN "线上" WHEN '11' THEN "线下" ELSE '-' END AS "billPayType", CASE b.is_urge WHEN '0' THEN "否" WHEN '1' THEN "是" ELSE '-' END AS "isUrge", b.urge_num as "urgeNum", b.is_remind as "isRemind", date_format(b.remind_time,'%Y-%m-%d %H:%i:%s') as "remindTime", date_format(b.created_time,'%Y-%m-%d %H:%i:%s') as "createdTime", b.creator_account as "creatorAccount", b.modifier_account as "modifierAccount", date_format(b.modified_time,'%Y-%m-%d %H:%i:%s') as "modifiedTime", CASE o.order_status WHEN '0' THEN "未支付" WHEN '1' THEN "已支付" ELSE '-' END AS "orderStatus", b.order_id as "orderId", o.order_num as "orderNum", o.order_name as "orderName", o.order_amount as "orderAmount", date_format(o.pay_time,'%Y-%m-%d %H:%i:%s') as "payTime", o.pay_id as "payId", o.pay_amount as "payAmount", CASE o.pay_type WHEN '0' THEN "微信" WHEN '1' THEN "支付宝" WHEN '2' THEN "银联" ELSE '-' END AS "payType", o.pay_remark as "payRemark" from tb_payment_bill b left join tb_payment_order o on b.order_id = o.id left join tb_payment_code c on c.id = b.bill_type where b.record_status = 1
and ( b.bill_name like CONCAT('%', #{searchCode,jdbcType=VARCHAR}, '%') or b.bill_obj_name like CONCAT('%', #{searchCode,jdbcType=VARCHAR}, '%') or b.bill_remark like CONCAT('%', #{searchCode,jdbcType=VARCHAR}, '%') )
and b.bill_id = #{billId,jdbcType=VARCHAR}
and b.bill_num = #{billNum,jdbcType=VARCHAR}
and b.bill_name = #{billName,jdbcType=VARCHAR}
and b.bill_type = #{billType,jdbcType=VARCHAR}
and b.bill_obj_id = #{billObjId,jdbcType=VARCHAR}
and b.bill_amount = #{billAmount,jdbcType=VARCHAR}
and b.bill_create_account = #{billCreateAccount,jdbcType=VARCHAR}
and b.bill_remark = #{billRemark,jdbcType=VARCHAR}
and b.bill_status = #{billStatus,jdbcType=VARCHAR}
and b.is_urge = #{isUrge,jdbcType=VARCHAR}
and b.bill_create_time > #{billCreateTimeBegin,jdbcType=VARCHAR}
and b.bill_create_time < #{billCreateTimeEnd,jdbcType=VARCHAR}
and b.bill_create_time > #{payEndTimeBegin,jdbcType=VARCHAR}
and b.pay_end_time < #{payEndTimeEnd,jdbcType=VARCHAR}
and b.bill_status not in ('0','-1')
order by #{orderBy,jdbcType=VARCHAR}

SELECT SUM(b.bill_amount) AS "billTypeAmount", DATE_FORMAT(b.bill_create_time, '%Y%m%d') AS "billDate", b.bill_type AS "billTypeId", c.code_value AS "billTypeName", (SELECT SUM(pb.bill_amount) FROM tb_payment_bill pb WHERE pb.bill_status NOT IN ('2', '4') AND pb.bill_type = billTypeId AND pb.bill_create_time <= STR_TO_DATE( CONCAT( DATE_FORMAT(b.bill_create_time, '%Y-%m-%d'), '23-59-59' ), '%Y-%m-%d %H-%i-%s' ) AND pb.bill_create_time >= STR_TO_DATE( CONCAT( DATE_FORMAT(b.bill_create_time, '%Y-%m-%d'), '00-00-00' ), '%Y-%m-%d %H-%i-%s' )) AS "billTypeUnpayAmount", (SELECT SUM(pb.bill_amount) FROM tb_payment_bill pb WHERE pb.bill_status IN ('2', '4') AND pb.bill_type = billTypeId AND pb.bill_create_time <= STR_TO_DATE( CONCAT( DATE_FORMAT(b.bill_create_time, '%Y-%m-%d'), '23-59-59' ), '%Y-%m-%d %H-%i-%s' ) AND pb.bill_create_time >= STR_TO_DATE( CONCAT( DATE_FORMAT(b.bill_create_time, '%Y-%m-%d'), '00-00-00' ), '%Y-%m-%d %H-%i-%s' )) AS "billTypePayAmount" FROM tb_payment_bill b LEFT JOIN tb_payment_code c ON b.bill_type = c.id WHERE b.record_status = 1 and b.bill_status not in ('0','-1')
and b.bill_create_time > #{billStartTime,jdbcType=VARCHAR}
and b.bill_create_time < #{billEndTime,jdbcType=VARCHAR}
and b.bill_type in
#{billTypeId}
GROUP BY billDate,billTypeId

SELECT
SUM(b.bill_amount) AS "totleAmount"
,(SELECT SUM(t.bill_amount) FROM tb_payment_bill t
    WHERE t.record_status = 1 and t.bill_status  NOT IN ('2', '4')

        and t.bill_create_time > #{billStartTime,jdbcType=VARCHAR}


        and t.bill_create_time < #{billEndTime,jdbcType=VARCHAR}


        and t.bill_type in

            #{billTypeId}


) as "totleUnpayAmountDaily"
,(SELECT SUM(t.bill_amount) FROM tb_payment_bill t
WHERE t.record_status = 1 and t.bill_status IN ('2', '4')

    and t.bill_create_time > #{billStartTime,jdbcType=VARCHAR}


    and t.bill_create_time < #{billEndTime,jdbcType=VARCHAR}


    and t.bill_type in

        #{billTypeId}


) as "totlePayAmountDaily"
FROM
tb_payment_bill b
WHERE b.record_status = 1 and b.bill_status not in ('0','-1')

    and b.bill_create_time > #{billStartTime,jdbcType=VARCHAR}


    and b.bill_create_time < #{billEndTime,jdbcType=VARCHAR}


    and b.bill_type in

        #{billTypeId}
