mapper.select:
select p.point_id as "pointId"
      ,u.account as "userAccount"
      ,p.point_num as "pointNum"
      ,p.freeze_num as "freezeNum"
      ,u.name as "name"
      ,u.nick_name as "nickName"
 from tb_user_point p  left join tb_user_person u on  p.user_account = u.account
 where p.record_status = '1'

    and b.point_id = #{pointId,jdbcType=VARCHAR}


    and u.account = #{userAccount,jdbcType=VARCHAR}


    and u.name = #{name,jdbcType=VARCHAR}


    and u.nick_name = #{nickName,jdbcType=VARCHAR}

mapper.insert:
insert into tb_service_org_team (id,org_id,con_name,con_position,con_quali,con_time,con_speciality, created_time,creator_account,modified_time,modifier_account,record_status) select A.* from(
( select #{item.id,jdbcType=VARCHAR} as "id" ,#{item.orgId,jdbcType=VARCHAR} as "orgId" ,#{item.conName,jdbcType=VARCHAR} as "conName" ,#{item.conPosition,jdbcType=VARCHAR} as "conPosition" ,#{item.conQuali,jdbcType=VARCHAR} as "conQuali" ,#{item.conTime,jdbcType=DATE} as "conTime" ,#{item.conSpeciality,jdbcType=VARCHAR} as "conSpeciality" ,#{item.createdTime,jdbcType=TIMESTAMP} as "createdTime" ,#{item.creatorAccount,jdbcType=VARCHAR} as "creatorAccount" ,#{item.modifiedTime,jdbcType=TIMESTAMP} as "modifiedTime" ,#{item.modifierAccount,jdbcType=VARCHAR} as "modifierAccount" ,#{item.recordStatus,jdbcType=TINYINT} as "recordStatus" from dual )
)A
